import React, { Component } from 'react';

import './App.css';
import Header from './Components/Header'
import Main from './Components/Main'
import Section from './Components/Section'
import HomePage from './HomePage'
import VideoList from './Components/VideoList'
import Upload from './Components/Upload'
import { BrowserRouter, Switch, Router, Route, Link } from "react-router-dom";
import axios from 'axios';

const myAPI = "?api_key=1a2da34e-4760-45ed-9b07-e9acc896e17f";
const videoListURL = "https://project-2-api.herokuapp.com/videos/"+ myAPI
const mainVideoURL =(videoID)=> `http://project-2-api.herokuapp.com/videos/${videoID}${myAPI}`



class App extends Component {
  state = {
    videos: [],
    nowPlayingVideo: null

      }

    componentDidMount() {
      axios
        .get(videoListURL)
        .then(response => {
          this.setState({
            videos: response.data
          });
        })
        .then(response => {
          const videoID = this.props.match.params.id;
          axios.get(mainVideoURL(videoID))
          .then(response => {
            this.setState({
              nowPlayingVideo: response.data.image

            });
          });
        });
    }

    componentDidUpdate(props) {
      axios
        .get(videoListURL)
        .then(response => {
          const videoID = props.videoID;

          axios.get(mainVideoURL(videoID))
          .then(response => {

            this.setState({
              nowPlayingVideo: response.data.image
            });
          });
        });
    }










  render() {
    return (
      <div>  
    <Header />
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/videos/:id" component={HomePage} render={(props => {
            return <HomePage {...props} />
          })} /> />
          <Route path="/upload" component={Upload} />

        </Switch>
      </div>
    )
  }
}

export default App;
