import React, { Component } from 'react';


import Main from '../Main'
import Section from '../Section'
import axios from 'axios';
import { BrowserRouter, Switch, Router, Route, Link } from "react-router-dom";


const myAPI = "?api_key=1a2da34e-4760-45ed-9b07-e9acc896e17f";
const videoListURL = "https://project-2-api.herokuapp.com/videos/"+ myAPI
const mainVideoURL =(data)=> `http://project-2-api.herokuapp.com/videos/${data.match.params.id}${myAPI}`


class HomePage extends React.Component {
constructor(props) {
  super(props);

  this.state = {
    videos: [],
    mainVideo: null,
    comments: []

      }

}


  componentDidMount() {
    axios
      .get(videoListURL)
      .then(response => {
        this.setState({
          videos: response.data
        });
      })
      .then(response => {
        const videoID = this.state.videos[0].id

        axios.get(mainVideoURL(videoID))
        .then(response => {
          console.log(response.data.views)
          this.setState({
            mainVideo: response.data.image,
            views: response.data.views,
            likes: response.data.likes,
            comments:response.data.comments,
            title: response.data.title,
            channel:response.data.channel,
            timestamp: response.data.timestamp
          });
        });
      });
  }

  componentDidUpdate(props) {
    axios
      .get(videoListURL)
      .then(response => {
        const videoID = props.videoID;

        axios.get(mainVideoURL(videoID))
        .then(response => {
          console.log(response.data.views)
          this.setState({
            mainVideo: response.data.image,
            views: response.data.views,
            likes: response.data.likes,
            comments:response.data.comments,
            title: response.data.title,
            channel:response.data.channel,
            timestamp: response.data.timestamp
          });
        });
      });
  }



render() {


  console.log(this.props.match.params.id);
  return (
    <div>
      <Main />
      <Section 
        videoID = {this.props.match.params.id}
        mainVideo={this.state.mainVideo}  
        views={this.state.views}  
        likes={this.state.likes}  
        comments={this.state.comments}  
        title={this.state.title}


      />
    </div>
  );
}
}
export default HomePage;
